using UnityEngine; using UnityEngine.SceneManagement; [RequireComponent(typeof(Rigidbody))]public class PlayerController : MonoBehaviour{public Vector3 jump; public float jumpForce = 2.0f; public float speed = 0.5f; public bool isGrounded; Rigidbody rb; void Start(){ rb = GetComponent<Rigidbody>(); jump = new Vector3(0.0f, 2.0f, 0.0f); } void OnCollisionStay(){ isGrounded = true; } void Update(){ transform.position += Vector3.left * speed * Time.deltaTime; if (Input.GetKeyDown(KeyCode.Space) || Input.touchCount > 0 || Input.GetMouseButtonDown(0)) { rb.AddForce(jump * jumpForce, ForceMode.Impulse); } } private void OnCollisionEnter(Collision collision){ if (collision.gameObject.tag == "MurderTheBird") { SceneManager.LoadScene("DedBird"); } }}